import React, { useEffect, useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { messageget, usersRouter, host } from "../utils/Api";
import Message from "../components/Message";
import Users from "../components/Users";
import { useNavigate } from "react-router-dom";
import Deconnect from "../components/Deconnect";
import Welcome from "../components/Welcome";
import { io } from "socket.io-client";
import { useRef } from "react";

const Chat = () => {
  const socket = useRef();
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [messages, setmessage] = useState();
  const [currentuser, setCurrentuser] = useState(undefined);
  const [currentId, setCurrentId] = useState(
    JSON.parse(localStorage.getItem("userId"))
  );
  const [toId, settoId] = useState(undefined);
  const [toUser, settoUser] = useState("Welcome");
  const [input, setInput] = useState("");
  let converse = [];
  let array = [];

  //recuperation de l'utilisateur courant
  //_______________________________________________________________________________________________________
  useEffect(() => {
    const confUser = async () => {
      if (!localStorage.getItem("user")) {
        navigate("/login");
      } else {
        setCurrentuser(await JSON.parse(localStorage.getItem("user")));
        setCurrentId(await JSON.parse(localStorage.getItem("userId")));
      }
    };
    confUser();
  }, []);
  useEffect(() => {
    if (currentuser) {
      socket.current = io(host);
      socket.current.emit("add-user", currentuser._id);
    }
  }, [currentuser]);
  //___________________________________________________________________________________________________________

  //recuperation des Users
  //_______________________________________________________________________________________________
  useEffect(() => {
    const token = JSON.parse(localStorage.getItem("token"));
    axios(usersRouter, {
      method: "GET",
      headers: {
        Authorization: "Bearer " + token,
      },
    })
      .then(function (response) {
        setUsers(response.data.allUsers);
      })
      .catch(function (error) {
        console.log(error);
      });
    //______________________________________________________________________________________________________

    //recuperations des messages
    //_______________________________________________________________________________________________________
    axios(`${messageget}${currentId}`, {
      method: "GET",
      headers: {
        Authorization: "Bearer " + token,
      },
    })
      .then(function (response) {
        setmessage(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  useEffect(() => {
    if (socket.current) {
      socket.current.on("msg-recieved", (input) => {
        setmessage({
          message: input,
        });
      });
    }
  }, [messages]);
  //_______________________________________________________________________________________________

  //filtre des messages
  //________________________________________________________________________________________________
  const myMessages = messages?.messages.filter((elm) => {
    array.push(elm.to, elm.from);
    converse = [...new Set(array)];
    if (elm.to === toId || elm.from === toId) {
      return true;
    } else return false;
  });

  //filtre user
  const myUsers = users?.filter((elm) => {
    if (converse.includes(elm._id)) {
      return true;
    } else {
      return false;
    }
  });

  //__________________________________________________________________________________________________

  //Envoi de message
  //_________________________________________________________________________________________________________
  const handleSubmit = async (event) => {
    event.preventDefault();

    const { data } = await axios.post(messageget, {
      message: input,
      from: currentId,
      to: toId,
      toname: toUser,
    });
    socket.current.emit("send-msg", {
      message: input,
      from: currentId,
      to: toId,
      toname: toUser,
    });
    console.log("messages envoyer");

    if (data.status === false) {
      console.log("une erreur c'est produite au niveau des data");
    } else {
      setInput("");
    }
  };
  const handleChange = (event) => {
    setInput(event.target.value);
  };
  //______________________________________________________________________________________________________
  //fonction de changement de chat

  const changeChat = (id, name) => {
    settoId(id);
    settoUser(name);
  };
  if (users === undefined) {
    return (
      <div className="fixed top-0 right-0 h-screen w-screen z-50 flex-col flex justify-center items-center">
        <div className="animate-spin rounded-full h-32 w-32 border-t-2 border-b-2 border-gray-900"></div>
        <br />
        Loading...
        <p className="w-1/3 text-center text-[gray]">
          This may take a few seconds, please don't close this page.
        </p>
      </div>
    );
  } else {
    return (
      <Container>
        <div className="flex h-screen antialiased text-gray-800">
          <div className="flex flex-row h-full w-full overflow-x-hidden">
            <div className="flex flex-col py-8 pl-6 pr-2 w-64 text-black bg-opacity-25 backdrop-filter backdrop-blur-lg flex-shrink-0">
              <div className="flex flex-row items-center  h-12 w-full ">
                <div className=" font-bold text-2xl">
                  <div className="brand flex">
                    <img src="lo.png" alt="" width={40} />
                    <h1 className="">
                      GOCHAT<sup className="text-[15px] ">42</sup>
                    </h1>
                  </div>
                </div>
              </div>

              <div className="overflow-hidden relative w-10 h-10  rounded-full  dark:bg-gray-600">
                <svg
                  className="absolute -left-1 w-12 h-12 text-black"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    fillRule="evenodd"
                    d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z"
                    clipRule="evenodd"
                  ></path>
                </svg>
              </div>
              <div className="text-sm font-semibold mt-2 text-black">
                <a href="/profil">{currentuser}</a>
              </div>
              <div className="text-xs text-gray-500 text-gray">Developper</div>

              <div className="flex flex-col mt-3">
                <div className="flex flex-row items-center justify-between text-xs">
                  <span className="font-bold">
                    <button className="bg-black hover:bg-white hover:text-black hover:border hover:border-black text-white font-bold py-2 px-2 rounded">
                      Conversations
                    </button>{" "}
                  </span>
                </div>

                <Users
                  users={myUsers}
                  changeChat={changeChat}
                  settoId={settoId}
                />
              </div>
              <Deconnect />
            </div>
            <div className="flex flex-col flex-auto h-full p-6 ">
              <div className="flex flex-col flex-auto flex-shrink-0   bg-[#e6e4e2] rounded-xl h-full  ">
                <button className="flex flex-row items-center   p-2">
                  <div className="overflow-hidden relative w-10 h-10 bg-opacity-25 backdrop-filter backdrop-blur-lg  rounded-full  dark:bg-gray-600">
                    <svg
                      className="absolute -left-1 w-12 h-12 text-black"
                      fill="currentColor"
                      viewBox="0 0 20 20"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        fillRule="evenodd"
                        d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z"
                        clipRule="evenodd"
                      ></path>
                    </svg>
                  </div>
                  <div className="font-medium text-black px-3 ">
                    <div>{toUser}</div>
                    <div className="text-sm text-gray-500   ">GoChat</div>
                  </div>
                </button>
                <div
                  id="contact"
                  className="flex flex-col h-full px-14 overflow-x-auto mb-4"
                >
                  {toId === undefined ? (
                    <Welcome />
                  ) : (
                    <Message
                      messages={myMessages}
                      toId={toId}
                      currentId={currentId}
                      toUser={toUser}
                    />
                  )}
                </div>
                <div className="flex flex-row items-center h-16 bg-opacity-25 backdrop-filter backdrop-blur-lg bg-black w-full px-4">
                  <div>
                    <button className="flex items-center justify-center text-gray-400 hover:text-gray-600">
                      <svg
                        className="w-5 h-5 text-white"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth="2"
                          d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13"
                        ></path>
                      </svg>
                    </button>
                  </div>
                  <div className="flex-grow ml-4">
                    <div className="relative w-full">
                      <input
                        name="message"
                        type="text"
                        value={input}
                        className="flex w-full border rounded-xl focus:outline-none focus:border-indigo-300 pl-4 h-10"
                        onChange={(e) => handleChange(e)}
                      />
                      <button className="absolute flex items-center justify-center h-full w-12 right-0 top-0 text-gray-400 hover:text-gray-600">
                        <svg
                          className="w-6 h-6"
                          fill="none"
                          stroke="currentColor"
                          viewBox="0 0 24 24"
                          xmlns="http://www.w3.org/2000/svg"
                        >
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth="2"
                            d="M14.828 14.828a4 4 0 01-5.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                          ></path>
                        </svg>
                      </button>
                    </div>
                  </div>
                  <div className="ml-4">
                    <button
                      type="submit"
                      onClick={(event) => handleSubmit(event)}
                      className="flex items-center justify-center     rounded-xl text-white px-4 py-1 flex-shrink-0"
                    >
                      <span className="ml-2">
                        <svg
                          className="w-8 h-8 transform rotate-45 -mt-px hover:text-black"
                          fill="none"
                          stroke="currentColor"
                          viewBox="0 0 24 24"
                          xmlns="http://www.w3.org/2000/svg"
                        >
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth="2"
                            d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8"
                          ></path>
                        </svg>
                      </span>
                    </button>
                  </div>
                </div>
              </div>
            </div>
            {/* //rightbar */}
            <div className="flex flex-col py-8 pl-6 pr-2 w-64 text-black bg-opacity-25 backdrop-filter backdrop-blur-lg flex-shrink-0">
              <div className="flex flex-col mt-3">
                <div className="flex flex-row items-center justify-between text-xs">
                  <span className="font-bold mx-r-3 w-[95%]">
                    <button className="bg-black w-[100%] hover:bg-white hover:text-black hover:border hover:border-black text-white font-bold py-2 px-4 mx-3 rounded">
                      All users
                    </button>
                  </span>
                </div>

                <div
                  id="contact"
                  className="flex flex-col space-y-1 mt-4 -mx-2 h-60 overflow-y-auto "
                >
                  {users &&
                    users.map((name, index) => {
                      return (
                        <button
                          key={index}
                          onClick={() => changeChat(name._id, name.username)}
                          className="flex flex-row items-center hover:border-b-2 hover:border-black border-b-[1px] border-[#f5f5f5]  p-2"
                        >
                          <div className="flex items-center justify-center h-8 w-8 bg-black rounded-full text-white">
                            {name.username[0]?.toUpperCase()}
                          </div>
                          <div className="ml-2 text-sm font-semibold">
                            {name.username}
                          </div>
                        </button>
                      );
                    })}
                </div>
              </div>
            </div>
          </div>
        </div>
      </Container>
    );
  }
};
