import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import styled from "styled-components";

import axios from "axios";
import { loginRouter } from "../utils/Api";

const Login = () => {
  const navigate = useNavigate();
  const [values, setValues] = useState({
    username: "",
    password: "",
  });
  useEffect(() => {
    if (localStorage.getItem("user")) {
      navigate("/");
    }
  });

  const handleSubmit = async (event) => {
    event.preventDefault();

    console.log("is in validation", loginRouter);
    const { password, username } = values;
    const { data } = await axios.post(loginRouter, {
      password,
      username,
    });
    if (data.status === false) {
      console.log("une erreur c'est produite au niveau des data");
    } else {
      console.log(data.token);
      localStorage.setItem("token", JSON.stringify(data.token));
      console.log("bien connecter");
      localStorage.setItem("user", JSON.stringify(data.user));
      localStorage.setItem("userId", JSON.stringify(data.userId));
      navigate("/");
    }
  };

  const handleChange = (event) => {
    setValues({ ...values, [event.target.name]: event.target.value });
  };
  return (
    <>
      <FormContainer>
        <form onSubmit={(event) => handleSubmit(event)}>
          <div className="brand">
            {/* <img src="logo.png" alt="" width={70} /> */}
            <h1>
              GoChat<span>42</span>
            </h1>
          </div>
          <label for="inp" class="inp">
            <input
              type="text"
              name="username"
              onChange={(e) => handleChange(e)}
              id="inp"
              placeholder="&nbsp;"
            />
            <span class="label">Username</span>
            <span class="focus-bg"></span>
          </label>
          <label for="inp" class="inp">
            <input
              type="password"
              name="password"
              onChange={(e) => handleChange(e)}
              id="inp"
              placeholder="&nbsp;"
            />
            <span class="label">Password</span>
            <span class="focus-bg"></span>
          </label>

          <button type="submit">Login</button>
          <span>
            Dont have an account ? <Link to={"/signup"}>Signup</Link>
          </span>
        </form>
        {/* <div className="text">
          <h1>Welcome Friend"</h1>
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Rem
            temporibus velit asperiores, blanditiis enim laboriosam doloremque!
          </p>
          <img src="s2.svg" alt="" width={500} />
        </div> */}
      </FormContainer>
    </>
  );
};

const FormContainer = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  /* flex-direction: column; */
  justify-content: center;
