import React from "react";

const Users = ({ users, changeChat, settoId }) => {
  return (
    <div
      id="contact"
      class="flex flex-col space-y-1 mt-4 -mx-2 h-60 overflow-y-auto "
    >
      {users &&
        users.map((name, index) => {
          return (
            <button
              onClick={() => changeChat(name._id, name.username)}
              class="flex flex-row items-center hover:border-b-2 hover:border-black border-b-[1px] border-black  p-2"
            >
              <div class="flex items-center justify-center h-8 w-8 bg-black rounded-full text-white">
                {name.username[0].toUpperCase()}
              </div>
              <div class="ml-2 text-sm font-semibold">{name.username}</div>
              {/* <div class="flex items-center justify-center ml-auto text-xs text-white bg-red-500 h-5 w-5 rounded-full leading-none">
                3
              </div> */}
            </button>
          );
        })}
    </div>
  );
};

export default Users;
