import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import styled from "styled-components";

import axios from "axios";
import { registerRouter } from "../utils/Api";

const Register = () => {
  const navigate = useNavigate();
  const [values, setValues] = useState({
    username: "",
    email: "",
    password: "",
    confirmpassword: "",
  });

  const handleSubmit = async (event) => {
    event.preventDefault();

    console.log("is in validation", registerRouter);
    const { password, username, email } = values;
    console.log(values);
    const { data } = await axios.post(registerRouter, {
      username,
      email,
      password,
    });
    if (data.status === false) {
      console.log("une erreur c'est produite au niveau des data");
    } else {
      console.log("bien enregistre");
      localStorage.setItem("chat-app-user", JSON.stringify(data.user));
      navigate("/login");
    }
  };

  const handleChange = (event) => {
    setValues({ ...values, [event.target.name]: event.target.value });
  };
  return (
    <>
      <Container>
        {/* <div className="text">
          <h1>Let's Get Started</h1>
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Rem
            temporibus velit asperiores, blanditiis enim laboriosam doloremque!
          </p>
          <img src="s1.svg" alt="" width={500} />
          <button className="mobile">
            <a href="#form">Get Started</a>{" "}
          </button>
        </div> */}

        <form id="form" onSubmit={(event) => handleSubmit(event)}>
          <div className="brand">
            <img src="log.png" alt="" width={40} />
            <h1>
              GoChat<sup>42</sup>
            </h1>
          </div>
          <label for="inp" class="inp">
            <input
              type="text"
              name="username"
              onChange={(e) => handleChange(e)}
              placeholder="&nbsp;"
            />
            <span class="label">Username</span>
            <span class="focus-bg"></span>
          </label>
          <label for="inp" class="inp">
            <input
              type="email"
              name="email"
              onChange={(e) => handleChange(e)}
              placeholder="&nbsp;"
            />
            <span class="label">Email</span>
            <span class="focus-bg"></span>
          </label>
          <label for="inp" class="inp">
            <input
              type="password"
              name="password"
              onChange={(e) => handleChange(e)}
              placeholder="&nbsp;"
            />
            <span class="label">Password</span>
            <span class="focus-bg"></span>
          </label>
          <label for="inp" class="inp">
            <input
              type="password"
              name="confirmpassword"
              onChange={(e) => handleChange(e)}
              placeholder="&nbsp;"
            />
            <span class="label">Confirmpassword</span>
            <span class="focus-bg"></span>
          </label>

          <button type="submit">signup</button>
          <span>
            Already have an account ? <Link to={"/login"}>Login</Link>
          </span>
        </form>
      </Container>
    </>
  );
};

const Container = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  /* flex-direction: column; */
  justify-content: center;
  gap: 1rem;
  align-items: center;
  color: black;
  /* background-color: #000000ce; */

  button {
