import React, { useEffect, useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { messageget, usersRouter, host } from "../utils/Api";
import Message from "../components/Message";
import Users from "../components/Users";
import { useNavigate } from "react-router-dom";
import Deconnect from "../components/Deconnect";
import Welcome from "../components/Welcome";
import { io } from "socket.io-client";
import { useRef } from "react";

const Chat = () => {
  const socket = useRef();
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [messages, setmessage] = useState();
  const [currentuser, setCurrentuser] = useState(undefined);
  const [currentId, setCurrentId] = useState(undefined);
  const [toId, settoId] = useState(undefined);
  const [toUser, settoUser] = useState("Welcome");
  var conversation = [];
  var unique = [];
  var conversations = [];

  const [input, setInput] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    const { data } = await axios.post(messageget, {
      message: input,
      from: currentId,
      to: toId,
      toname: toUser,
    });
    socket.current.emit("send-msg", {
      message: input,
      from: currentId,
      to: toId,
      toname: toUser,
    });
    console.log("messages envoyer");

    if (data.status === false) {
      console.log("une erreur c'est produite au niveau des data");
    } else {
      setInput("");
    }
  };
  const handleChange = (event) => {
    setInput(event.target.value);
  };

  const changeChat = (id, name) => {
    settoId(id);
    settoUser(name);
  };

  useEffect(() => {
    const confUser = async () => {
      if (!localStorage.getItem("user")) {
        navigate("/login");
      } else {
        setCurrentuser(await JSON.parse(localStorage.getItem("user")));
        setCurrentId(await JSON.parse(localStorage.getItem("userId")));
      }
    };
    confUser();
  }, []);
  useEffect(() => {
    if (currentuser) {
      socket.current = io(host);
      socket.current.emit("add-user", currentuser._id);
    }
  }, [currentuser]);
  useEffect(() => {
    const token = JSON.parse(localStorage.getItem("token"));
    axios(usersRouter, {
      method: "GET",
      headers: {
        Authorization: "Bearer " + token,
      },
    })
      .then(function (response) {
        setUsers(response.data.allUsers);
      })
      .catch(function (error) {
        console.log(error);
      });

    axios(messageget, {
      method: "GET",
      headers: {
        Authorization: "Bearer " + token,
      },
    })
      .then(function (response) {
        setmessage(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, [messages]);

  useEffect(() => {
    if (socket.current) {
      socket.current.on("msg-recieved", (input) => {
        setmessage({
          message: input,
        });
      });
    }
  }, [messages]);
  const myMessages = messages?.message.filter((elm) => {
    if (
      (elm.to === toId && elm.from === currentId) ||
      (elm.to === currentId && elm.from === toId)
    ) {
      return true;
    } else return false;
  });
  const myConversation = () => {
    messages?.message.map((elm) => {
      conversation.push(elm.to);
      unique = [...new Set(conversation)];
    });
    return unique;
  };
  console.log(unique);
  const conver = users.map((elm) => {
    for (let i = 0; i < unique.length; i++) {
      if (elm._id === unique[i]) {
        conversations.push(elm);
      } else {
        return false;
      }
    }
    return conversations;
  });
  console.log(conver);
  if (users === undefined || messages === undefined) {
    return (
      <div class="fixed top-0 right-0 h-screen w-screen z-50 flex-col flex justify-center items-center">
        <div class="animate-spin rounded-full h-32 w-32 border-t-2 border-b-2 border-gray-900"></div>
        <br />
        Loading...
        <p class="w-1/3 text-center text-[gray]">
          This may take a few seconds, please don't close this page.
        </p>
      </div>
    );
  } else {
    return (
      <Container>
        <div class="flex h-screen antialiased text-gray-800">
          <div class="flex flex-row h-full w-full overflow-x-hidden">
