import React, { useEffect, useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { messageget, usersRouter } from "../utils/Api";
import Message from "../components/Message";
import Users from "../components/Users";
import { useNavigate } from "react-router-dom";
import Deconnect from "../components/Deconnect";

const Chat = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [messages, setmessage] = useState();
  const [currentuser, setCurrentuser] = useState(undefined);
  const [currentId, setCurrentId] = useState(undefined);
  const [toId, settoId] = useState(undefined);
  const [toUser, settoUser] = useState("Welcome");
  const [values, setValues] = useState({
    message: "",
  });

  const handleSubmit = async (event) => {
    event.preventDefault();

    const { message } = values;
    const { data } = await axios.post(messageget, {
      message: message,
      from: currentId,
      to: toId,
    });
    if (data.status === false) {
      console.log("une erreur c'est produite au niveau des data");
    } else {
    }
  };
  const handleChange = (event) => {
    setValues({ ...values, [event.target.name]: event.target.value });
  };
  const changeChat = (id, name) => {
    settoId(id);
    settoUser(name);
  };

  useEffect(() => {
    const confUser = async () => {
      if (!localStorage.getItem("user")) {
        navigate("/login");
      } else {
        setCurrentuser(await JSON.parse(localStorage.getItem("user")));
        setCurrentId(await JSON.parse(localStorage.getItem("userId")));
      }
    };
    confUser();
  }, []);
  useEffect(() => {
    const token = JSON.parse(localStorage.getItem("token"));
    axios(usersRouter, {
      method: "GET",
      headers: {
        Authorization: "Bearer " + token,
      },
    })
      .then(function (response) {
        // handle success
        setUsers(response.data.allUsers);
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      });

    axios(messageget, {
      method: "GET",
      headers: {
        Authorization: "Bearer " + token,
      },
    })
      .then(function (response) {
        // handle success
        setmessage(response.data);
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      });
  }, []);
  const myMessages = messages?.message.filter((elm) => {
    if (
      (elm.to === toId && elm.from === currentId) ||
      (elm.to === currentId && elm.from === toId)
    )
      return true;
    else return false;
  });
  // {$or:[{from: '636c070eaa2ce6a0b07afc9f'}, {to:'636c070eaa2ce6a0b07afc9f'}]}
  console.log(myMessages);
  return (
    <Container>
      <div class="flex h-screen antialiased text-gray-800">
        <div class="flex flex-row h-full w-full overflow-x-hidden">
          <div class="flex flex-col py-8 pl-6 pr-2 w-64 text-black bg-white flex-shrink-0">
            <div class="flex flex-row items-center  h-12 w-full my-3">
              <div class=" font-bold text-2xl">
                <div className="brand">
                  {/* <img src="logo.png" alt="" width={30} /> */}
                  <h1 class="">
                    GOCHAT<sup class="text-[15px] ">42</sup>
                  </h1>
                </div>
              </div>
            </div>

            <div class="overflow-hidden relative w-10 h-10  rounded-full  dark:bg-gray-600">
              <svg
                class="absolute -left-1 w-12 h-12 text-black"
                fill="currentColor"
                viewBox="0 0 20 20"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  fill-rule="evenodd"
                  d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z"
                  clip-rule="evenodd"
                ></path>
              </svg>
            </div>
            <div class="text-sm font-semibold mt-2 text-black">
              <a href="/profil">{currentuser}</a>
            </div>
            <div class="text-xs text-gray-500 text-gray">Developper</div>

            <div class="flex flex-col mt-8">
              <div class="flex flex-row items-center justify-between text-xs">
                <span class="font-bold">
                  <button class="bg-black hover:bg-white hover:text-black hover:border hover:border-black text-white font-bold py-2 px-2 rounded">
                    Conversations
                  </button>{" "}
                  <button class="bg-black hover:bg-white hover:text-black hover:border hover:border-black text-white font-bold py-2 px-4 mx-3 rounded">
                    All users
                  </button>{" "}
                </span>
              </div>
              <Users users={users} changeChat={changeChat} settoId={settoId} />
            </div>
            <Deconnect />
          </div>
          <div class="flex flex-col flex-auto h-full p-6 ">
            <div class="flex flex-col flex-auto flex-shrink-0  bg-[#f5f5f5] rounded-xl h-full p-4 ">
              <button class="flex flex-row items-center  bg-white ">
                <div class="overflow-hidden relative w-10 h-10  rounded-full  dark:bg-gray-600">
                  <svg
                    class="absolute -left-1 w-12 h-12 text-black"
                    fill="currentColor"
                    viewBox="0 0 20 20"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      fill-rule="evenodd"
                      d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z"
                      clip-rule="evenodd"
                    ></path>
                  </svg>
                </div>
                <div class="font-medium text-black px-3 ">
                  <div>{toUser}</div>
                  <div class="text-sm text-gray-500   ">GoChat</div>
                </div>
              </button>
              <div
                id="contact"
                class="flex flex-col h-full overflow-x-auto mb-4"
              >
                <Message
                  messages={myMessages}
                  toId={toId}
                  currentId={currentId}
                />
              </div>
              <div class="flex flex-row items-center h-16 rounded-xl bg-black w-full px-4">
                <div>
                  <button class="flex items-center justify-center text-gray-400 hover:text-gray-600">
                    <svg
                      class="w-5 h-5"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        stroke-linecap="round"
                        stroke-linejoin="round"
                        stroke-width="2"
                        d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13"
                      ></path>
                    </svg>
                  </button>
                </div>
                <div class="flex-grow ml-4">
                  <div class="relative w-full">
                    <input
                      name="message"
                      type="text"
                      class="flex w-full border rounded-xl focus:outline-none focus:border-indigo-300 pl-4 h-10"
                      onChange={(e) => handleChange(e)}
                    />
